using Microsoft.AspNetCore.Mvc;
using System.Web.Http;
using HttpGetAttribute = Microsoft.AspNetCore.Mvc.HttpGetAttribute;
using RouteAttribute = Microsoft.AspNetCore.Mvc.RouteAttribute;

namespace ecomerce_react_js_with_asp_dotnet.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class ProductsController : ControllerBase
    {
        public List<ProductsModel> products = new List<ProductsModel>(){
            new ProductsModel()
            {
                ImageUrl = "data:image/jpeg;base64,",
                ProductName = "Banana",
                PriceString = "R",
                PriceInt = 20
            },
            new ProductsModel()
            {
                ImageUrl = "data:image/jpeg;base64,",
                ProductName = "Apple",
                PriceString = "R",
                PriceInt = 20
            },
            new ProductsModel()
            {
                ImageUrl = "data:image/jpeg;base64,",
                ProductName = "Strawberry",
                PriceString = "R",
                PriceInt = 20
            },
            new ProductsModel()
            {
                ImageUrl = "data:image/jpeg;base64,",
                ProductName = "Orange",
                PriceString = "R",
                PriceInt = 20,
            },
        };

        private readonly ILogger<ProductsController> _logger;

        public ProductsController(ILogger<ProductsController> logger)
        {
            _logger = logger;
        }

        [HttpGet]
        public IEnumerable<ProductsModel> Get()
        {
            return products.ToArray();
        }
        [HttpGet("search-array")]
        public IEnumerable<ProductsModel> SearchArray([FromQuery] string search = "all")
        {
            if(search == "all")
            {
                return products.ToArray();
            }
            else
            {
                var query = search.ToLower();
                var filteredItems = products.Where<ProductsModel>((product) => product.ProductName.ToLower().Contains(query));
                return filteredItems.ToArray();
            }
        }
        [HttpGet("upload-data")]
        public IEnumerable<ProductsModel> UploadData([FromUri] ProductsModel product)
        {
            if (product != null)
            {
                products.Add(product);
                return products.ToArray();
            }
            else
            {
                return products.ToArray();

            }
        }
    }
}
